import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {
  bookTitle: string = '';
  book: any = null;
  errorMessage: string = '';

  constructor(private http: HttpClient) {}

  getBookByTitle() {
    this.book = null;
    this.errorMessage = '';

    if (!this.bookTitle.trim()) {
      this.errorMessage = 'Please enter a book title.';
      return;
    }

    this.http
      .get<any>(`http://localhost:8080/books/${this.bookTitle}`)
      .subscribe({
        next: (data) => {
          this.book = data;
        },
        error: (err) => {
          this.errorMessage = 'Book not found or server error.';
        },
      });
  }
}
<div style="max-width: 500px; margin: 5rem auto; padding: 2rem; border-radius: 20px; background: #ffffffaa; backdrop-filter: blur(10px); box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15); text-align: center; font-family: 'Poppins', sans-serif;">

  <h2 style="font-size: 2rem; font-weight: 700; color: #4b0082; margin-bottom: 1.5rem;">
    üìö Book Finder
  </h2>

  <input
    [(ngModel)]="bookTitle"
    type="text"
    placeholder="Enter book title..."
    style="width: 100%; padding: 0.9rem; font-size: 1rem; border-radius: 12px; border: 2px solid #6a0dad; margin-bottom: 1.2rem; outline: none;"
  />

  <button
    (click)="getBookByTitle()"
    style="width: 100%; padding: 0.9rem; font-size: 1rem; border: none; border-radius: 12px; background: linear-gradient(to right, #6a0dad, #ff69b4); color: white; font-weight: bold; cursor: pointer;"
  >
    üîç Search
  </button>

  <div
    *ngIf="book"
    style="margin-top: 2rem; padding: 1.5rem; background: #f0f8ff; border-left: 5px solid #ff69b4; border-radius: 15px; text-align: left; box-shadow: 0 4px 20px rgba(255, 105, 180, 0.2);"
  >
    <h3 style="color: #6a0dad; margin-bottom: 0.5rem;">FindByBookTitle:{{ book.bookTitle }}</h3>
    <p><strong>Book ID:</strong> {{ book.bookId }}</p>
    <p><strong>Book Title:</strong> {{ book.bookTitle }}</p>
    <p><strong>Author:</strong> {{ book.author }}</p>
    <p><strong>Price:</strong> ‚Çπ{{ book.bookPrice }}</p>

  </div>

  <div
    *ngIf="errorMessage"
    style="margin-top: 1rem; padding: 1rem; background-color: #ffe6e6; color: #d8000c; border-radius: 10px; font-weight: bold;"
  >
    ‚ö†Ô∏è {{ errorMessage }}
  </div>
</div>
