import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {
  bookTitle: string = '';
  book: any = null;
  errorMessage: string = '';

  constructor(private http: HttpClient) {}

  getBookByTitle() {
    this.book = null;
    this.errorMessage = '';

    if (!this.bookTitle.trim()) {
      this.errorMessage = 'Please enter a book title.';
      return;
    }

    this.http
      .get<any>(`http://localhost:8080/books/${this.bookTitle}`)
      .subscribe({
        next: (data) => {
          this.book = data;
        },
        error: (err) => {
          this.errorMessage = 'Book not found or server error.';
        },
      });
  }
}
<div style="max-width: 500px; margin: 5rem auto; padding: 2rem; border-radius: 20px; background: #ffffffdd; backdrop-filter: blur(14px); box-shadow: 0 12px 35px rgba(0, 0, 0, 0.2); text-align: center; font-family: 'Poppins', sans-serif; position: relative;">

  <!-- Header -->
  <h2 style="font-size: 2.2rem; font-weight: 800; color: #6a0dad; margin-bottom: 1.5rem; text-shadow: 1px 1px #ffccff;">
    üìö Book Finder
  </h2>

  <!-- Input Field -->
  <input
    [(ngModel)]="bookTitle"
    type="text"
    placeholder="Enter book title..."
    style="width: 100%; padding: 1rem; font-size: 1rem; border-radius: 14px; border: 2px solid #b266ff; margin-bottom: 1.2rem; outline: none; box-shadow: inset 0 2px 6px rgba(106, 13, 173, 0.1); transition: 0.3s ease;"
  />

  <!-- Search Button -->
  <button
    (click)="handleSearch()"
    style="width: 100%; padding: 1rem; font-size: 1rem; border: none; border-radius: 14px; background: linear-gradient(to right, #6a0dad, #ff69b4); color: white; font-weight: bold; cursor: pointer; transition: 0.3s ease; box-shadow: 0 6px 16px rgba(255, 105, 180, 0.3);"
    onmouseover="this.style.transform='scale(1.03)'"
    onmouseout="this.style.transform='scale(1)'"
  >
    üîç Search
  </button>


  <!-- Book Details -->
  <div
    *ngIf="book"
    style="margin-top: 2rem; padding: 1.5rem; background: linear-gradient(to right, #fef3f9, #e6e6ff); border-radius: 20px; box-shadow: 0 4px 20px rgba(255, 105, 180, 0.2); text-align: left; font-size: 1rem; color: #4b0082; line-height: 1.6;"
  >
    <h3 style="text-align: center; font-size: 1.5rem; font-weight: 700; margin-bottom: 1rem;">
      üìñ Book Details
    </h3>
    <p><strong>üìò Title:</strong> {{ book.bookTitle }}</p>
    <p><strong>üë§ Author:</strong> {{ book.author }}</p>
    <p><strong>üí∞ Price:</strong> ‚Çπ{{ book.bookPrice }}</p>
    <p><strong>üÜî Book ID:</strong> {{ book.bookId }}</p>
  </div>

  <!-- Backend Error -->
  <div
    *ngIf="errorMessage"
    style="margin-top: 1rem; padding: 1rem; background-color: #ffe6e6; color: #d8000c; border-radius: 10px; font-weight: bold;"
  >
    ‚ö†Ô∏è {{ errorMessage }}
  </div>
</div>
