import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {
  bookTitle: string = '';
  book: any = null;
  errorMessage: string = '';

  constructor(private http: HttpClient) {}

  getBookByTitle() {
    this.book = null;
    this.errorMessage = '';

    if (!this.bookTitle.trim()) {
      this.errorMessage = 'Please enter a book title.';
      return;
    }

    this.http
      .get<any>(`http://localhost:8080/books/${this.bookTitle}`)
      .subscribe({
        next: (data) => {
          this.book = data;
        },
        error: (err) => {
          this.errorMessage = 'Book not found or server error.';
        },
      });
  }
}
<div class="app-container">
  <h2 class="title">ðŸ“˜ Search Book</h2>

  <input
    [(ngModel)]="bookTitle"
    type="text"
    class="input"
    placeholder="Enter book title"
  />

  <button class="btn" (click)="getBookByTitle()">Search</button>

  <div *ngIf="book" class="card">
    <h3>{{ book.bookTitle }}</h3>
    <p><strong>Author:</strong> {{ book.author }}</p>
    <p><strong>Price:</strong> â‚¹{{ book.bookPrice }}</p>
    <p><strong>ID:</strong> {{ book.bookId }}</p>
  </div>

  <div *ngIf="errorMessage" class="error">
    {{ errorMessage }}
  </div>
</div>
