import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {
  bookTitle: string = '';
  book: any = null;
  errorMessage: string = '';

  constructor(private http: HttpClient) {}

  getBookByTitle() {
    this.book = null;
    this.errorMessage = '';

    if (!this.bookTitle.trim()) {
      this.errorMessage = 'Please enter a book title.';
      return;
    }

    this.http
      .get<any>(`http://localhost:8080/books/${this.bookTitle}`)
      .subscribe({
        next: (data) => {
          this.book = data;
        },
        error: (err) => {
          this.errorMessage = 'Book not found or server error.';
        },
      });
  }
}
<div style="max-width: 500px; margin: 5rem auto; padding: 2rem; border-radius: 20px; background: #ffffffcc; backdrop-filter: blur(10px); box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15); text-align: center; font-family: 'Poppins', sans-serif; position: relative; overflow: hidden;">

  <h2 style="font-size: 2.2rem; font-weight: 800; color: #4b0082; margin-bottom: 1.5rem;">
    üìö Book Finder
  </h2>

  <input
    [(ngModel)]="bookTitle"
    type="text"
    placeholder="Enter book title..."
    style="width: 100%; padding: 1rem; font-size: 1rem; border-radius: 14px; border: 2px solid #6a0dad; margin-bottom: 1.2rem; outline: none; box-shadow: 0 2px 10px rgba(106, 13, 173, 0.1);"
  />

  <!-- Button Wrapper with Relative Positioning -->
  <div style="position: relative; display: inline-block; width: 100%; margin-bottom: 1rem;">
    <!-- Anime Girl Image on Top of Button -->
    <img src="https://i.pinimg.com/originals/f2/46/0b/f2460bdb1e4e1b46f30b0ef89a0b28d9.jpg" 
         alt="Anime Girl"
         style="position: absolute; top: -50px; right: -30px; width: 80px; border-radius: 50%; box-shadow: 0 4px 15px rgba(255,105,180,0.4); transition: transform 0.4s ease; z-index: 10;"
         onmouseover="this.style.transform='scale(1.1)'"
         onmouseout="this.style.transform='scale(1)'"/>

    <button
      (click)="getBookByTitle()"
      style="width: 100%; padding: 1rem; font-size: 1rem; border: none; border-radius: 14px; background: linear-gradient(to right, #6a0dad, #ff69b4); color: white; font-weight: bold; cursor: pointer; box-shadow: 0 4px 14px rgba(255, 105, 180, 0.3); transition: transform 0.3s ease;"
      onmouseover="this.style.transform='scale(1.03)'"
      onmouseout="this.style.transform='scale(1)'"
    >
      üîç Search
    </button>
  </div>

  <div
    *ngIf="book"
    style="margin-top: 2rem; padding: 1.5rem; background: #f0f8ff; border-left: 5px solid #ff69b4; border-radius: 15px; text-align: left; box-shadow: 0 4px 20px rgba(255, 105, 180, 0.2);"
  >
    <h3 style="color: #6a0dad; margin-bottom: 0.5rem;">{{ book.bookTitle }}</h3>
    <p><strong>Author:</strong> {{ book.author }}</p>
    <p><strong>Price:</strong> ‚Çπ{{ book.bookPrice }}</p>
    <p><strong>Book ID:</strong> {{ book.bookId }}</p>
  </div>

  <div
    *ngIf="errorMessage"
    style="margin-top: 1rem; padding: 1rem; background-color: #ffe6e6; color: #d8000c; border-radius: 10px; font-weight: bold;"
  >
    ‚ö†Ô∏è {{ errorMessage }}
  </div>
</div>
