import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {
  bookTitle: string = '';
  book: any = null;
  errorMessage: string = '';

  constructor(private http: HttpClient) {}

  getBookByTitle() {
    this.book = null;
    this.errorMessage = '';

    if (!this.bookTitle.trim()) {
      this.errorMessage = 'Please enter a book title.';
      return;
    }

    this.http
      .get<any>(`http://localhost:8080/books/${this.bookTitle}`)
      .subscribe({
        next: (data) => {
          this.book = data;
        },
        error: (err) => {
          this.errorMessage = 'Book not found or server error.';
        },
      });
  }
}
<div class="container" style="max-width: 500px; margin: auto; padding: 2rem;">
  <h2>üîç Find Book by Title</h2>

  <input
    [(ngModel)]="bookTitle"
    type="text"
    placeholder="Enter book title"
    class="form-control"
    style="margin-bottom: 1rem;"
  />

  <button (click)="getBookByTitle()" class="btn btn-primary w-100">
    Search Book
  </button>

  <div *ngIf="book" class="card mt-4 p-3 shadow">
    <h4>{{ book.bookTitle }}</h4>
    <p><strong>Author:</strong> {{ book.author }}</p>
    <p><strong>Price:</strong> ‚Çπ{{ book.bookPrice }}</p>
    <p><strong>ID:</strong> {{ book.bookId }}</p>
  </div>

  <div *ngIf="errorMessage" class="alert alert-danger mt-3">
    {{ errorMessage }}
  </div>
</div>
